from threading import Thread
from pymemri.plugin.schema import PluginRun
from pymemri.pod.client import PodClient
import flask
import multiprocessing
from flask import render_template
from time import sleep
import os
from pymemri.plugin.states import RUN_USER_ACTION_NEEDED, RUN_USER_ACTION_COMPLETED
from pymemri.plugin.pluginbase import POD_PLUGIN_DNS_ENV

app = flask.Flask(__name__, template_folder='template')
qr_code_dict = None

QR_CODE_KEY = "qr_code"


@app.route('/qr')
def index():
    global qr_code_dict
    qr_code_data = qr_code_dict[QR_CODE_KEY]
    done = qr_code_dict.get("authenticated", False)
    if done:
        return render_template("success.html")
    else:
        return render_template('images.html', chart_output=qr_code_data)

def run_app(client, plugin_run, auth_url, qr_dict, host="0.0.0.0", port=8080):
    global qr_code_dict
    qr_code_dict = qr_dict

    Thread(check_status, args=(plugin_run, client, auth_url), daemon=True).start()
    app.run(host=host, port=port)

def check_status(plugin_run, client, auth_url):
    # Redirect to the served auth url
    plugin_run.authUrl = auth_url
    plugin_run.status = RUN_USER_ACTION_NEEDED
    client.update_item(plugin_run)
    print("QR SERVER REQUIRE ACTION")

	# Poll until status == "ready"
    while not qr_code_dict.get("authenticated", False):
        sleep(1)
    print("QR SERVER AUTHENTICATED")
    plugin_run.status = RUN_USER_ACTION_COMPLETED
    client.update_item(plugin_run)

def run_qr_flow(_qr_code_data, client: PodClient, plugin_run: PluginRun):
    manager = multiprocessing.Manager()
    process_dict = manager.dict()
    process_dict["qr_code"] = _qr_code_data
    process_dict["authenticated"] = False
    host = "0.0.0.0"
    port = 8080
    user_host = os.environ.get(POD_PLUGIN_DNS_ENV, f"http://0.0.0.0:{port}")
    auth_url = f"{user_host}/qr"
    process = multiprocessing.Process(target=run_app, args=(client, plugin_run, auth_url, process_dict),
                                      kwargs={"host": host, "port": port}, daemon=True)
    process.start()

    print(f"GO TO {auth_url} and scan the code")
    return process, process_dict

if __name__ == "__main__":
    # this is here for testing purposes, no function in production
    qr_code_data ="data:image/svg+xml;base64,"
    process = run_qr_flow(qr_code_data)
    i=0
    while i<3:
        i+=1
        sleep(1)
        print("waiting")
    